package br.com.each.modelo.atividadefisica;import br.com.each.modelo.atividade.Atividade;import br.com.each.modelo.paciente.Paciente;public class AtividadeFisicaRepresentation {	private Long id;	private Atividade atividade;	private Integer horaInicial;	private Integer horaFinal;	private Integer minutoInicial;	private Integer minutoFinal;	private Integer dia;	private DiaDaSemana diaDaSemana;	private String descricao;	private Double caloria;	private String corIntensidade;	private Integer tempo;	public AtividadeFisicaRepresentation(AtividadeFisica atividadeFisica, Paciente paciente) {		this.id = atividadeFisica.getId();		this.atividade = atividadeFisica.getAtividade();		this.horaInicial = atividadeFisica.getHoraInicial();		this.horaFinal = atividadeFisica.getHoraFinal();		this.minutoInicial = atividadeFisica.getMinutoInicial();		this.minutoFinal = atividadeFisica.getMinutoFinal();		this.dia = atividadeFisica.getDia();		this.diaDaSemana = atividadeFisica.getDiaDaSemana();		this.descricao = atividadeFisica.getDescricao();		this.caloria = atividadeFisica.getCaloriaPorAtividadeFisica(paciente);		this.corIntensidade = atividadeFisica.getCorIntensidade();		this.tempo = atividadeFisica.getTempo();	}	public Atividade getAtividade() {		return atividade;	}	public String getCorIntensidade() {		return corIntensidade;	}	public Double getCaloria() {		return caloria;	}	public Long getId() {		return id;	}	public Integer getHoraInicial() {		return horaInicial;	}	public Integer getHoraFinal() {		return horaFinal;	}	public Integer getMinutoInicial() {		return minutoInicial;	}	public Integer getMinutoFinal() {		return minutoFinal;	}	public Integer getDia() {		return dia;	}	public DiaDaSemana getDiaDaSemana() {		return diaDaSemana;	}	public String getDescricao() {		return descricao;	}	public Integer getTempo() {		return tempo;	}}