package br.com.each.modelo.paciente;public class PacienteRepresentation {	private Long id;	private String nome;	private Double caloriaTotal;	private Double tempoTotal;	private Double caloriaLeve;	private Double tempoLeve;	private Double caloriaModerado;	private Double tempoModerado;	private Double caloriaVigoroso;	private Double tempoVigoroso;	private Integer idade;	private Double peso;	private Double altura;	private Double basalHarrisBenedict;	private Double basalFAO;	private Double basal;	public PacienteRepresentation() {	}	public PacienteRepresentation(Paciente paciente) {		this.id = paciente.getId();		this.nome = paciente.getNome();		this.caloriaTotal = paciente.getCaloriaTotal();		this.tempoTotal = paciente.getTempoTotal();		this.caloriaLeve = paciente.getCaloriaLeve();		this.tempoLeve = paciente.getTempoLeve();		this.caloriaModerado = paciente.getCaloriaModerado();		this.tempoModerado = paciente.getTempoModerado();		this.caloriaVigoroso = paciente.getCaloriaVigoroso();		this.tempoVigoroso = paciente.getTempoVigoroso();		this.idade = paciente.getIdade();		this.peso = paciente.getPeso();		this.altura = paciente.getAltura();		this.basalHarrisBenedict = paciente.getBasalHarrisBenedict();		this.basalFAO = paciente.getBasalFAO();	}	public void setId(Long id) {		this.id = id;	}	public void setNome(String nome) {		this.nome = nome;	}	public void setCaloriaTotal(Double caloriaTotal) {		this.caloriaTotal = caloriaTotal;	}	public void setTempoTotal(Double tempoTotal) {		this.tempoTotal = tempoTotal;	}	public void setCaloriaLeve(Double caloriaLeve) {		this.caloriaLeve = caloriaLeve;	}	public void setTempoLeve(Double tempoLeve) {		this.tempoLeve = tempoLeve;	}	public void setCaloriaModerado(Double caloriaModerado) {		this.caloriaModerado = caloriaModerado;	}	public void setTempoModerado(Double tempoModerado) {		this.tempoModerado = tempoModerado;	}	public void setCaloriaVigoroso(Double caloriaVigoroso) {		this.caloriaVigoroso = caloriaVigoroso;	}	public void setTempoVigoroso(Double tempoVigoroso) {		this.tempoVigoroso = tempoVigoroso;	}	public void setIdade(Integer idade) {		this.idade = idade;	}	public void setPeso(Double peso) {		this.peso = peso;	}	public void setAltura(Double altura) {		this.altura = altura;	}	public void setBasalHarrisBenedict(Double basalHarrisBenedict) {		this.basalHarrisBenedict = basalHarrisBenedict;	}	public void setBasalFAO(Double basalFAO) {		this.basalFAO = basalFAO;	}	public void setBasal(Double basal) {		this.basal = basal;	}	public Long getId() {		return id;	}	public String getNome() {		return nome;	}	public Double getCaloriaTotal() {		return caloriaTotal;	}	public Double getTempoTotal() {		return tempoTotal;	}	public Double getCaloriaLeve() {		return caloriaLeve;	}	public Double getTempoLeve() {		return tempoLeve;	}	public Double getCaloriaModerado() {		return caloriaModerado;	}	public Double getTempoModerado() {		return tempoModerado;	}	public Double getCaloriaVigoroso() {		return caloriaVigoroso;	}	public Double getTempoVigoroso() {		return tempoVigoroso;	}	public Integer getIdade() {		return idade;	}	public Double getPeso() {		return peso;	}	public Double getAltura() {		return altura;	}	public Double getBasalHarrisBenedict() {		return basalHarrisBenedict;	}	public Double getBasalFAO() {		return basalFAO;	}	public Double getBasal() {		return basal;	}}