package br.com.each.util;import java.text.MessageFormat;import java.text.SimpleDateFormat;import java.util.ResourceBundle;import org.joda.time.LocalDate;import br.com.caelum.vraptor.Convert;import br.com.caelum.vraptor.Converter;import br.com.caelum.vraptor.converter.ConversionError;import br.com.caelum.vraptor.ioc.ApplicationScoped;@Convert(LocalDate.class)@ApplicationScopedpublic class LocalDateConverter implements Converter<LocalDate> {	public LocalDateConverter() {	}	public LocalDate convert(String value, Class<? extends LocalDate> type, ResourceBundle bundle) {		try {			return new LocalDate(new SimpleDateFormat("dd/mm/yyyy").parseObject(value));		} catch (Exception e) {			throw new ConversionError(MessageFormat.format(bundle.getString("is_not_a_valid_date"), value));		}	}}